import java.util.*;public class Main {    public static void main(String [] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        ArrayList<Integer> L = new ArrayList<>();        for(int i=0;i<n;i++) L.add(sc.nextInt());        int bit [] = new int[n+1];        Arrays.fill(bit,0);        for(int i=0;i<n;i++) bit = add(i+1,L.get(i),bit,n);        for(int i=1;i<=n;i++) System.out.println(sum(i,bit));    }    public static int sum(int i,int bit[]){        int sum = 0;        while(i>0){            sum+=bit[i];            i -= i&-i;        }        return sum;    }    public static int[] add(int i,int x,int bit[],int n){        while(i<=n){            bit[i]+= x;            i+= (i & -i);        }        return bit;    }}