import java.util.*;public class Main{    public static void main(String [] args) {        Scanner sc = new Scanner(System.in);        long x = sc.nextLong();        StringBuilder sol = new StringBuilder(Long.toBinaryString(x));        for(int i=0;i<32;i++)sol.insert(0,0);        System.out.println(sol.substring(sol.length()-32));        sol = new StringBuilder(Long.toBinaryString(~x));        System.out.println(sol.substring(32));        sol = new StringBuilder(Long.toBinaryString(x << 1));        for (int i = 0; i < 32; i++) sol.insert(0, 0);        System.out.println(sol.substring(sol.length()-32));        sol = new StringBuilder(Long.toBinaryString(x >> 1));        for (int i = 0; i < 32; i++) sol.insert(0, 0);        System.out.println(sol.substring(sol.length()-32));    }}