import java.util.*;public class Main{    public static void main(String [] args) {        Scanner sc = new Scanner(System.in);        long a = sc.nextLong();        long b = sc.nextLong();        long sol = a&b;        StringBuilder sb = new StringBuilder(Long.toBinaryString(sol));        for(int i=0;i<32;i++) sb.insert(0,0);        System.out.println(sb.substring(sb.length()-32));        sol = a|b;        sb = new StringBuilder(Long.toBinaryString(sol));        for(int i=0;i<32;i++)sb.insert(0,0);        System.out.println(sb.substring(sb.length()-32));        sol = a^b;        sb = new StringBuilder(Long.toBinaryString(sol));        for(int i=0;i<32;i++)sb.insert(0,0);        System.out.println(sb.substring(sb.length()-32));    }}