import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int m = sc.nextInt();        int [] L = new int[n+1];        Arrays.fill(L,999999999);        L[0] = 0;        for(int i=0;i<m;i++){            int v = sc.nextInt();            for(int j=v;j<n+1;j++) L[j] = Math.min(L[j-v]+1,L[j]);        }        System.out.println(L[n]);    }}