import java.util.*;public class Main {    public static void main(String [] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        long [] L = new long[n];        for(int i=0;i<n;i++)L[i] = sc.nextLong();        int q = sc.nextInt();        for(int i=0;i<q;i++){            long k = sc.nextLong();            int left = 0, right = n;            int lb = n,ub = n;            while(left<right){                int mid = (left+right)/2;                if(L[mid]>=k){                    lb = mid;                    right = mid;                }                else left = mid+1;            }            left = lb;            right =n;            while(left<right){                int mid = (left+right)/2;                if(L[mid]>k){                    ub = mid;                    right = mid;                }                else left = mid+1;            }            System.out.println(lb+" "+ub);        }    }}