import java.math.BigInteger;import java.util.*;public class Main{    public static void main(String [] args) {        Scanner sc = new Scanner(System.in);        int N = sc.nextInt();        int W = sc.nextInt();        long [] T = new long[200001];        Arrays.fill(T,0);        int min_t=200000,max_t=0;        for(int i=0;i<N;i++){            int s = sc.nextInt();            int t = sc.nextInt();            int p = sc.nextInt();            T[s]+=p;            T[t]-=p;            if(min_t>s) min_t = s;            if(max_t<t) max_t = t;        }        boolean check = true;        for(int i=min_t;i<=max_t;i++){            if(i!=0)T[i] += T[i-1];            if(T[i]>W){                check = false;                break;            }        }        if(check) System.out.println("Yes");        else System.out.println("No");    }}