import java.util.*;public class Main {    public static void main(String [] args) {        Scanner sc = new Scanner(System.in);        StringBuilder A = new StringBuilder();        StringBuilder B = new StringBuilder();        int n = sc.nextInt();        for(int i=0;i<n;i++) A.append(sc.nextInt());        int m =sc.nextInt();        for(int i=0;i<m;i++) B.append(sc.nextInt());        String a = new String(A);        String b = new String(B);        if(a.compareTo(b)<0) System.out.println(1);        else System.out.println(0);    }}