import java.util.*;public class Main {    public static void main(String [] args) {        Scanner sc = new Scanner(System.in);        int q = sc.nextInt();        LinkedList<Integer> L = new LinkedList<>();        ListIterator<Integer> it = L.listIterator();        for(int i=0;i<q;i++){            int cmd = sc.nextInt();            if(cmd==0) {                it.add(sc.nextInt());                it.previous();            }            else if(cmd==1){                int d = sc.nextInt();                if(d>0)for(int j=0;j<d;j++)it.next();                else{                    d = Math.abs(d);                    for(int j=0;j<d;j++)it.previous();                }            }            else{                it.next();                it.remove();            }        }        StringBuilder opt = new StringBuilder();        for(int i:L) opt.append(i+"\n");        System.out.print(opt);        }}