import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int N = sc.nextInt();        int W = sc.nextInt();        int [][] L = new int[N+1][W+1];        /*配列の初期化*/        for(int i=0;i<N+1;i++){            Arrays.fill(L[i],0);        }        /*Knapsack*/        for(int i=0;i<N;i++) {            int v = sc.nextInt();            int w = sc.nextInt();            for (int j = 0; j < W + 1; j++) {                L[i + 1][j] = Math.max(L[i + 1][j], L[i][j]);                if (j - w >= 0) {                    L[i + 1][j] = Math.max(L[i + 1][j], L[i][j - w] + v);                    L[i + 1][j] = Math.max(L[i + 1][j], L[i + 1][j - w] + v);                }                if (j - 1 >= 0) {                    L[i + 1][j] = Math.max(L[i + 1][j], L[i + 1][j - 1]);                }            }        }        System.out.println(L[N][W]);    }}