import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        long n = sc.nextInt();        StringBuilder sb = new StringBuilder();        sb.append(n+":");        for(int i=2;i<=Math.sqrt(n)+1;i++){            while(true){                if(n%i==0){                    n/=i;                    sb.append(" "+i);                }                else break;            }        }        if(n!=1) sb.append(" "+n);        System.out.println(sb);    }}