import java.util.*;public class Main {    public static void main(String [] args) {        Scanner sc = new Scanner(System.in);        Set<Long> set = new HashSet<>();        int q = sc.nextInt();        for(int i=0;i<q;i++){            int cmd = sc.nextInt();            long query = sc.nextLong();            if(cmd==0){                set.add(query);                System.out.println(set.size());            }            else{                if(set.contains(query)) System.out.println(1);                else System.out.println(0);            }        }    }}