import java.util.*;public class Main {    public static void main(String [] args){        Scanner sc = new Scanner(System.in);        String T = sc.next();        String P = sc.next();        char last_char = P.charAt(P.length()-1);        StringBuilder OPT = new StringBuilder();        for(int i=P.length()-1;i<T.length();){            System.out.println(i);            if(T.charAt(i)==last_char&&T.substring((i-P.length()+1),i+1).equals(P))OPT.append((i - P.length() + 1) + "\n");            if(P.lastIndexOf(T.charAt(i))==-1) i+=P.length();            else i+=(P.length()-1-P.lastIndexOf(T.charAt(i)));        }        System.out.print(OPT);    }}