import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int a = sc.nextInt();        int b = sc.nextInt();        int [] XY = new int[2];        extGCD(a,b,XY);        System.out.println(XY[0]+" "+XY[1]);    }    public static int extGCD(int a,int b,int [] XY){        if(b==0){            XY[0] = 1;            XY[1] = 0;            return 1;        }        XY = Swap(XY);        int d = extGCD(b,a%b,XY);        XY = Swap(XY);        XY[1]-=a/b*XY[0];        return d;    }    public static int [] Swap(int [] L){        int x = L[0];        L[0] = L[1];        L[1] = x;        return L;    }}